from   dlpx.virtualization.platform   import   Plugin  from   generated.definitions   import   SnapshotDefinition  plugin   =   Plugin ()  @plugin . linked . post_snapshot ()  def   linked_post_snapshot ( staged_source ,   repository ,   source_config ,   optional_snapshot_parameters ): 
